import React, { useEffect, useState } from 'react';
import './App.css';
import Tmdb from './Tmdb';
import MovieRow from './components/MoviesRow';
import FilmeDestaque from './components/FilmeDestaque';



export default () => {

  const [movieList, setMovieList] = useState([]);
  const [destaqData, setDestaqData] = useState([]);

  useEffect(()=>{
    const loadAll = async () => {
      let list = await Tmdb.getHomeList();
      setMovieList(list);

      let lancamentos = list.filter(i=>i.slug === 'lancamentos');
      let esclhaFilme = Math.floor(Math.random() * (lancamentos[0].items.results.lenght -1));
      let esc = lancamentos[0].items.results[esclhaFilme];

      console.log(esc);

    }

    loadAll();

  }, []);

  return(
    <div className="page">

    {
      destaqData &&
       <FilmeDestaque item={destaqData} />
    }



      <section className="lists">
          {movieList.map((item, key)=>(
            <MovieRow key={key} title={item.title} items={item.items}/>
          ))}
      </section>
    </div>
  );
}
